import React, {useState, useEffect} from 'react';

const RAND_CHAR_POOL =
  '!"#$%&\'()*+,-./0123456789:;<=>?@ABCDEFGHIJKLMNOPQRSTUVWXYZ[\\]^_`abcdefghijklmnopqrstuvwxyz{|}~';
const randC = () =>
  RAND_CHAR_POOL.charAt(Math.floor(Math.random() * RAND_CHAR_POOL.length));
const randS = l =>
  Array(l)
    .fill(' ')
    .map(randC)
    .join('');

const timerFunction = (cb, cbFinal) => () => {
  cb();
  setTimeout(() => {
    cb();
    setTimeout(() => {
      cb();
      setTimeout(() => {
        cb();
        setTimeout(() => {
          cbFinal();
        }, 400);
      }, 200);
    }, 100);
  }, 50);
};

const INITIAL_TIMEOUT = 50;

const sc = props => {
  const [c, setC] = useState(props.c);
  useEffect(() => {
    const timer = setTimeout(
      timerFunction(
        () => setC(randC),
        () => setC(props.c),
      ),
      INITIAL_TIMEOUT,
    );
    return () => clearTimeout(timer);
  }, [props.c]);
  return <span>{c}</span>;
};

const Scramble = props => {
  const cs = Array.from(props.s).map((c, i) => <sc c={c} key={i} />);
  return <span>{cs}</span>;
};

export default Scramble;
