import React, {useState, useEffect} from 'react';

const RAND_CHAR_POOL =
  '!"#$%&\'()*+,-./0123456789:;<=>?@ABCDEFGHIJKLMNOPQRSTUVWXYZ[\\]^_`abcdefghijklmnopqrstuvwxyz{|}~';
const randC = () =>
  RAND_CHAR_POOL.charAt(Math.floor(Math.random() * RAND_CHAR_POOL.length));
const randS = l =>
  Array(l)
    .fill(' ')
    .map(randC)
    .join('');

const INITIAL_TIMEOUT = 50;
const timeout = n => INITIAL_TIMEOUT * n ** 5 + Math.random() * INITIAL_TIMEOUT;

const timerFunction = (cb, cbFinal) => () => {
  cb();
  setTimeout(() => {
    cb();
    setTimeout(() => {
      cb();
      setTimeout(() => {
        cb();
        setTimeout(() => {
          cbFinal();
        }, timeout(4));
      }, timeout(3));
    }, timeout(2));
  }, timeout(1));
};

const ScrambleC = props => {
  const [c, setC] = useState(props.c);
  useEffect(() => {
    const timer = setTimeout(
      timerFunction(
        () => setC(randC),
        () => setC(props.c),
      ),
      INITIAL_TIMEOUT + Math.random() * INITIAL_TIMEOUT,
    );
    return () => clearTimeout(timer);
  }, [props.c]);
  return <span>{c}</span>;
};

const Scramble = props => {
  const cs = Array.from(props.s).map((c, i) => <ScrambleC c={c} key={i} />);
  console.log(cs);
  return <span>{cs}</span>;
};

export default Scramble;
