import 'intersection-observer';
import {InView} from 'react-intersection-observer';

import Scramble from '../components/Scramble';

export default function Index() {
  return (
    <>
      <InView>
        {({inView, ref, entry}) => (
          <div ref={ref} className="fade-in-on-load">
            <Scramble s="Hello, my name is Kevin Lee." />
            <br />
            <br />
            <Scramble s="I go by my Chinese name, 梦远 MNGYUAN." />
          </div>
        )}
      </InView>
      <style jsx>{`
        html {
          font-family: 'Space Mono', 'Noto Sans Mono CJK SC', monospace;
        }

        .fade-in-on-load {
          opacity: ${inView ? 1 : 0};
          transition: opacity 0.6s ease, top 0.6s ease;
          position: relative;
          top: ${inView ? 0 : 12}px;
          will-change: opacity, top;
        }
      `}</style>
    </>
  );
}
