const FadeIn = props => (
  <InView>
    {({inView, ref, entry}) => (
      <div ref={ref} className="fade-in-on-load">
        {props.children}
        <style jsx>{`
          .fade-in-on-load {
            opacity: ${inView ? 1 : 0};
            transition: opacity 0.6s ease, top 0.6s ease;
            position: relative;
            top: ${inView ? 0 : 12}px;
            will-change: opacity, top;
          }
        `}</style>
      </div>
    )}
  </InView>
);
export default FadeIn;
