import 'intersection-observer';
import {InView} from 'react-intersection-observer';

import Scramble from '../components/Scramble';

const FadeIn = props => (
  <InView>
    {({inView, ref, entry}) => (
      <div ref={ref} className="fade-in-on-load">
        {props.children}
        <style jsx>{`
          .fade-in-on-load {
            opacity: ${inView ? 1 : 0};
            transition: opacity 0.6s ease, top 0.6s ease;
            position: relative;
            top: ${inView ? 0 : 12}px;
            will-change: opacity, top;
          }
        `}</style>
      </div>
    )}
  </InView>
);

export default function Index() {
  return (
    <>
      <FadeIn>
        <Scramble s="Hello, my name is Kevin Lee." />
      </FadeIn>
      <br />
      <FadeIn>
        <Scramble s="I go by my Chinese name, MNGYUAN." />
      </FadeIn>
      <style jsx global>{`
        html {
          font-family: 'IBM Plex Mono', 'Space Mono', 'Noto Sans Mono CJK SC',
            monospace;
        }
      `}</style>
    </>
  );
}
